mapper.select:
select *
from item_basket
where id = #{id}
and seq = #{seq}

select *
from item_basket
where id = #{id}
and num = #{num}
and opt1 = #{opt1}
and opt2 = #{opt2}

mapper.update:
update item_basket
set qty = #{qty}, opt1 = #{opt1}, opt2 = #{opt2}, opt3 = #{opt3}, coupon_chk = #{coupon_chk}
where id = #{id} and seq = #{seq};

mapper.delete:
delete
from item_basket
where id = #{id}
and seq = #{seq};

delete
from item_basket
where id = #{id}
and num = #{num}
and name = #{name}
and opt1 = #{opt1}
and opt2 = #{opt2}
and opt3 = #{opt3}
