mapper.select:
SELECT COUNT(*)
FROM ord_detail
WHERE ord_num = #{ord_num}

select *
from ord_detail
WHERE id = #{id}
  AND ord_state NOT IN ('구매확정', '배송완료', '주문완료', '주문대기');

mapper.update:
update ord_detail
set opt1 = #{opt1},  opt2 = #{opt2}
where ord_num = #{ord_num} and seq = #{seq}

mapper.select:
select *
from ord_detail
where ord_num = #{ord_num} and seq = #{seq}

SELECT *
FROM ord_detail
where id = #{id}
ORDER BY ord_num DESC
    LIMIT #{offset},#{pageSize}

mapper.update:
