mapper.select:
SELECT COUNT(*)
FROM ord_detail
WHERE ord_num = #{ord_num}

select *
from ord_detail
WHERE id = #{id}
  AND ord_state NOT IN ('구매완료', '배송완료', '주문완료');

mapper.update:
update ord_detail
set opt1 = #{opt1},  opt2 = #{opt2}
where ord_num = #{ord_num} and seq = #{seq}

